<span class="line">        <span class="token class-name">System</span><span class="token punctuation">.</span>out<span class="token punctuation">.</span><span class="token function">println</span><span class="token punctuation">(</span><span class="token string">&quot;Static&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
